(function () {
  const IFRAME_PATH = "/admin/knowledge-base-embeded"; // Ğ¿ÑƒÑ‚ÑŒ Ñ„Ñ€Ğ¾Ğ½Ñ‚Ğ° Ğ‘Ğ—
  const PROBE_TIMEOUT_MS = 7000;
  const DEBUG = true;

  let step = 0;
  const tag = "[KB-embed]";
  const now = () => new Date().toISOString();
  const log  = (...a) => DEBUG && console.log(tag, (++step), now(), ...a);

  // --- Ğ¿Ğ¾Ğ¸ÑĞº ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚Ğ¾Ğ² Ñ ÑƒÑ‡Ñ‘Ñ‚Ğ¾Ğ¼ shadow DOM ---
  function find_in_tree(root_node, id) {
    if (root_node && typeof root_node.getElementById === "function") {
      const hit = root_node.getElementById(id); if (hit) return hit;
    }
    if (root_node && root_node.querySelector) {
      try { const hit = root_node.querySelector(`#${CSS.escape(id)}`); if (hit) return hit; } catch {}
    }
    const root_el = (root_node instanceof Document ? root_node.documentElement : root_node);
    const walker = document.createTreeWalker(root_el, NodeFilter.SHOW_ELEMENT, null);
    let node = walker.currentNode;
    while (node) {
      if (node.shadowRoot) {
        const hit = find_in_tree(node.shadowRoot, id);
        if (hit) return hit;
      }
      node = walker.nextNode();
    }
    return null;
  }
  function wait_for_elements(ids, on_ready) {
    const try_get = () => {
      const got = {}; let all = true;
      for (const id of ids) {
        const el = find_in_tree(document, id);
        got[id] = el; if (!el) all = false;
      }
      log("probe elements:", Object.fromEntries(Object.entries(got).map(([k,v]) => [k, !!v])));
      if (all) { obs.disconnect(); on_ready(got); }
    };
    const obs = new MutationObserver(() => try_get());
    obs.observe(document.documentElement || document, { childList: true, subtree: true });
    try_get();
  }

  // --- UI helpers ---
  function show_iframe(iframe, img) {
    img.style.display = "none";
    iframe.style.display = "block";
  }
  function show_error_image(iframe, img, reason, error_box, text) {
    try { iframe.removeAttribute("src"); } catch {}
    iframe.style.display = "none";
    img.style.borderRadius = "16px";
    img.style.display = "block";
    if (error_box && text) { error_box.textContent = text; error_box.style.display = "block"; }
  }

  // --- Ğ±Ñ‹ÑÑ‚Ñ€Ğ°Ñ ÑĞµÑ‚. Ğ¿Ñ€Ğ¾Ğ²ĞµÑ€ĞºĞ° Ğ±ĞµĞ· CORS ---
  async function probe_reachability(url) {
    const ctrl = new AbortController();
    const to = setTimeout(() => ctrl.abort(), PROBE_TIMEOUT_MS);
    try {
      const res = await fetch(url, { method: "GET", mode: "no-cors", signal: ctrl.signal });
      clearTimeout(to);
      return { reachable: true, type: res.type };
    } catch (e) {
      clearTimeout(to);
      return { reachable: false, error: e?.name || String(e) };
    }
  }

  // --- Frappe RPC ---
  function call_whitelist(method, args = {}) {
    return new Promise((resolve, reject) => {
      frappe.call({
        method,
        type: "POST",
        args,
        async: true,
        callback: (r) => resolve(r?.message || r),
        error: (xhr) => reject(new Error(xhr?.responseText || "RPC error"))
      });
    });
  }

  // --- ensure_token: link_check -> get_token (ĞµÑĞ»Ğ¸ Ğ½ÑƒĞ¶Ğ½Ğ¾) ---
  async function ensure_token() {
    const check = await call_whitelist("dantist_app.api.auth.handlers.link_check");
    if (check?.ok) return true;
    const got = await call_whitelist("dantist_app.api.auth.handlers.get_token");
    if (!got?.ok) throw new Error("Token fetch failed");
    const recheck = await call_whitelist("dantist_app.api.auth.handlers.link_check");
    return !!recheck?.ok;
  }

  // --- main ---
  wait_for_elements(["kb-frame", "kb-error-img", "kb-error-text"], async ({ "kb-frame": iframe, "kb-error-img": img, "kb-error-text": err_box }) => {
    log("init");

    // 0) origin Ñ„Ñ€Ğ¾Ğ½Ñ‚Ğ°
    let origin;
    try {
      const o = await call_whitelist("dantist_app.api.auth.handlers.get_iframe_origin");
      if (!o?.ok || !o?.origin) throw new Error("No origin");
      origin = o.origin.replace(/\/$/, "");
    } catch (_) {
      show_error_image(iframe, img, "origin fetch failed", err_box, "Failed to get iframe origin.");
      return;
    }

    // 1) Ğ²Ğ°Ğ»Ğ¸Ğ´Ğ½Ğ°Ñ ĞºÑƒĞºĞ° access_token
    try {
      const ok = await ensure_token();
      if (!ok) throw new Error("Token not ready");
    } catch (_) {
      show_error_image(iframe, img, "token ensure failed", err_box, "Authorization failed.");
      return;
    }

    // 2) reachability
    const url = `${origin}${IFRAME_PATH}`;
    const pr = await probe_reachability(url);
    if (!pr.reachable) {
      show_error_image(iframe, img, "network unreachable", err_box, "Connection failed: server is unreachable.");
      return;
    }

    // 3) Ğ³Ñ€ÑƒĞ·Ğ¸Ğ¼ iFrame
    try {
      iframe.addEventListener("load", () => show_iframe(iframe, img), { once: true });
      iframe.addEventListener("error", () => show_error_image(iframe, img, "iframe error", err_box, "Failed to load the embedded page."), { once: true });
      iframe.src = url;
      show_iframe(iframe, img);
    } catch (e) {
      show_error_image(iframe, img, "setting src exception", err_box, e?.message || "Initialization error");
    }

    // Ğ´Ğ¸Ğ°Ğ³Ğ½Ğ¾ÑÑ‚Ğ¸ĞºĞ° postMessage (Ğ¿Ğ¾ Ğ¶ĞµĞ»Ğ°Ğ½Ğ¸Ñ)
    window.addEventListener("message", (e) => {
      // if (e.origin !== origin) return;
      log("postMessage:", { origin: e.origin, data: e.data });
    });
  });
})();

