// Dentist â€¢ Bot Setup Teaser â€” theme-safe + instant repaint + i18n
(function (root) {
  if (!root) return;

  // --- utils ----------------------------------------------------
  var esc = (window.frappe && frappe.utils && frappe.utils.escape_html) ? frappe.utils.escape_html : (s)=>String(s);

  function safe(obj, path, dflt){ dflt = dflt===void 0 ? "" : dflt;
    try{ var cur=obj, parts=path.split("."); for(var i=0;i<parts.length;i++){ if(cur==null) return dflt; cur=cur[parts[i]]; }
      return (cur===undefined||cur===null) ? dflt : cur;
    } catch(e){ return dflt; }
  }

  function readCookie(name){
    var m = document.cookie.match(new RegExp("(?:^|;\\s*)" + name + "=([^;]+)"));
    return m ? decodeURIComponent(m[1]) : "";
  }

  function mapCode(s){
    var v = String(s || "").toLowerCase();
    if (v.indexOf("ru")===0) return "ru";
    if (v.indexOf("uk")===0 || v.indexOf("ua")===0) return "uk";
    if (v.indexOf("pl")===0) return "pl";
    if (v.indexOf("ka")===0 || v.indexOf("geor")>-1) return "ka";
    if (v.indexOf("be")===0) return "be";
    return "en";
  }

  function pickLang(){
    var fb   = window.frappe || {};
    var boot = fb.boot || {};
    var usr  = boot.user || {};
    var defs = usr.defaults || usr.user_defaults || {};
    var pool = [
      { src: "user.language",              code: usr.language || "" },
      { src: "user.user_language",         code: usr.user_language || "" },
      { src: "user.defaults.language",     code: defs.language || "" },
      { src: "user.defaults.lang",         code: defs.lang || "" },
      { src: "cookie.preferred_language",  code: readCookie("preferred_language") },
      { src: "cookie.lang",                code: readCookie("lang") },
      { src: "boot.lang",                  code: boot.lang || "" },
      { src: "sysdefaults.language",       code: safe(boot, "sysdefaults.language", "") },
      { src: "navigator.languages[0]",     code: (navigator.languages && navigator.languages[0]) || "" },
      { src: "navigator.language",         code: navigator.language || "" }
    ];
    var chosen = "en", chosenSrc = "fallback.en";
    for (var i=0;i<pool.length;i++){
      var raw = String(pool[i].code || "").trim();
      if (!raw) continue;
      chosen = mapCode(raw); chosenSrc = pool[i].src; break;
    }
