style:
/* Dentist â€¢ Bot Setup Teaser â€” neutral, responsive, theme-aware */
.bot-teaser-root {
  max-width: 980px;
  margin: 0 auto;
  padding: 16px;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto,
               "Helvetica Neue", Arial, sans-serif;
  line-height: 1.6;
  font-size: 16px;
  /* Ñ‡Ğ¸Ñ‚Ğ°ĞµĞ¼ Ñ†Ğ²ĞµÑ‚Ğ° Ğ¸Ğ· Ñ‚ĞµĞ¼Ñ‹: Ñ‡Ñ‚Ğ¾Ğ±Ñ‹ ÑÑ€Ğ°Ğ·Ñƒ Ğ¾Ğ±Ğ½Ğ¾Ğ²Ğ»ÑĞ»Ğ¾ÑÑŒ Ğ¿Ñ€Ğ¸ ÑĞ¼ĞµĞ½Ğµ Ñ‚ĞµĞ¼Ñ‹ */
  color: var(--text-color, #111827);
}

.bot-teaser {
  display: flex;
  gap: 16px;
  align-items: center;
  flex-wrap: wrap;
  border: 1px solid var(--border-color, #e5e7eb);
  border-radius: 12px;
  padding: 16px;
  box-shadow: var(--shadow-base, 0 4px 16px rgba(0,0,0,.05));
  background: var(--card-bg, #fff);
  transition: background .15s ease, border-color .15s ease, box-shadow .15s ease, color .15s ease;
}

.bot-teaser__left {
  flex: 0 0 220px;
  max-width: 220px;
  width: 100%;
}

.bot-teaser__media {
  border-radius: 12px;
  overflow: hidden;
  background: var(--control-bg, #f8fafc);
  position: relative;
}
.bot-teaser__media::before {
  content: "";
  display: block;
  width: 100%;
  padding-bottom: 75%; /* 4:3 aspect */
}
.bot-teaser__media img {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
}

.bot-teaser__right {
  flex: 1 1 320px;
  min-width: 260px;
}

.bot-teaser__title {
  margin: 0 0 8px;
  font-weight: 600;
  color: var(--text-color, #111827);
}

.bot-teaser__text {
  margin: 0;
  color: var(--text-muted, #6b7280);
}

/* ĞĞµĞ±Ğ¾Ğ»ÑŒÑˆĞ°Ñ Ğ°Ğ´Ğ°Ğ¿Ñ‚Ğ°Ñ†Ğ¸Ñ Ğ½Ğ° ÑƒĞ·ĞºĞ¸Ñ… ÑĞºÑ€Ğ°Ğ½Ğ°Ñ… */
@media (max-width: 520px) {
  .bot-teaser__left { flex-basis: 100%; max-width: 100%; }
}

